// src/components/TaskForm.js
import React, { useState } from 'react';
import { taskService } from '../services/api';

const TaskForm = ({ onTaskAdded }) => {
  const [title, setTitle] = useState('');
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!title.trim()) return;
    
    setSubmitting(true);
    setError(''); // Réinitialiser les erreurs précédentes
    
    try {
      const newTask = await taskService.createTask({ title });
      setTitle('');
      if (onTaskAdded) onTaskAdded(newTask); // Ajouter la nouvelle tâche dans l'état parent
    } catch (error) {
      setError('Erreur lors de la création de la tâche');
      console.error('Erreur:', error);
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div className="mb-6">
      <h2 className="text-xl font-semibold text-blue-800 mb-3">Ajouter une tâche</h2>
      <form onSubmit={handleSubmit} className="flex flex-col space-y-3">
        <input
          type="text"
          placeholder="Nouvelle tâche"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          disabled={submitting}
          className="px-4 py-2 border border-blue-200 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all"
        />
        <button 
          type="submit" 
          disabled={submitting}
          className="bg-gradient-to-r from-blue-500 to-blue-700 text-white font-medium py-2 px-4 rounded-md hover:from-blue-600 hover:to-blue-800 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-opacity-50 transition-all disabled:opacity-70"
        >
          {submitting ? 'Ajout...' : 'Ajouter'}
        </button>
      </form>
      {error && (
        <div className="mt-3 text-red-600 bg-red-100 p-2 rounded-md text-sm">
          {error}
        </div>
      )}
    </div>
  );
};

export default TaskForm;
