// src/services/api.js
export const taskService = {
  createTask: async (taskData) => {
    console.log("Creating task with data:", taskData);  // Log the task data being sent

    const response = await fetch('https://webappbackend.netlify.app/.netlify/functions/tasks', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(taskData),
    });

    // Log the response status and body
    console.log("Response status:", response.status);
    const responseBody = await response.json();
    console.log("Response body:", responseBody);

    if (!response.ok) {
      throw new Error(`Error creating task: ${responseBody.error || responseBody.message}`);
    }

    return responseBody;  // Return the created task
  },
};
