// src/components/TaskList.js
import React, { useState, useEffect } from 'react';
import { taskService } from '../services/api';

const TaskList = ({ tasks, onTaskDelete, onTaskComplete }) => {
  const handleComplete = async (taskId) => {
    try {
      const updatedTask = await taskService.completeTask(taskId);
      onTaskComplete(taskId); // Update the parent component's task list
    } catch (error) {
      console.error('Erreur lors de la mise à jour de la tâche:', error);
    }
  };

  const handleDelete = async (taskId) => {
    try {
      await taskService.deleteTask(taskId);
      onTaskDelete(taskId); // Update the parent component's task list
    } catch (error) {
      console.error('Erreur lors de la suppression de la tâche:', error);
    }
  };

  if (!tasks || tasks.length === 0) {
    return (
      <div className="task-list">
        <h2>Mes tâches</h2>
        <div className="empty-tasks">Aucune tâche pour le moment</div>
      </div>
    );
  }

  return (
    <div className="task-list">
      <h2>Mes tâches</h2>
      <ul>
        {tasks.map((task) => (
          <li key={task.id} className="task-item">
            <span
              style={{
                textDecoration: task.completed ? 'line-through' : 'none',
                opacity: task.completed ? 0.7 : 1,
              }}
            >
              {task.title}
            </span>
            <div className="task-actions">
              <button
                className="complete-button"
                onClick={() => handleComplete(task.id)}
                disabled={task.completed}
              >
                ✓
              </button>
              <button
                className="delete-button"
                onClick={() => handleDelete(task.id)}
              >
                ✕
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TaskList;
