// src/components/TaskList.js
import React from 'react';

const TaskList = ({ tasks }) => {
  if (!tasks || tasks.length === 0) {
    return (
      <div className="mt-6 text-center py-4 text-gray-500 bg-gray-50 rounded-md">
        Aucune tâche pour le moment
      </div>
    );
  }

  return (
    <div className="mt-6">
      <h2 className="text-xl font-semibold text-blue-800 mb-3">Mes tâches</h2>
      <ul className="space-y-2">
        {tasks.map((task) => (
          <li 
            key={task.id} 
            className="p-3 bg-gradient-to-r from-blue-50 to-blue-100 border-l-4 border-blue-500 rounded-md shadow-sm flex justify-between items-center"
          >
            <span>{task.title}</span>
            <div className="flex space-x-2">
              <button className="text-blue-600 hover:text-blue-800">
                ✓
              </button>
              <button className="text-red-600 hover:text-red-800">
                ✕
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TaskList;
